  color: $brand-color;
  transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1),
    color 250ms cubic-bezier(0.4, 0, 0.2, 1), 250ms;

  &:hover,
  :focus {
    background: $background-color-hover;
  }
  &--current {
    background: $brand-color;
    color: #ffffff;
    &:hover,
    &:focus {
      background: $brand-color;
      color: #ffffff;
    }
  }

  @media screen and (max-width: 767px) {
    font-size: 12px;
    width: 162px;
    height: 44px;
    &-label {
      margin-right: 8px;
    }
    &:not(:last-child) {
      margin-bottom: 10px;
    }
  }
  @media screen and (min-width: 768px) {
    font-size: 14px;
    width: 176px;
    height: 44px;

    &-label {
      margin-right: 10px;
    }
    &--current {
      width: 192px;
      height: 44px;
    }
    &:not(:last-child) {
      margin-right: 15px;
    }
  }
}

.contacts__svg {
  width: 8px;
  height: 8px;
  fill: none;
  stroke: $brand-color;

  &--current {
    stroke: #ffffff;
  }
}

sources.sass/_footer.scss:
.footer {
  padding-top: 3px;
  background-color: $background_main-color;
}

.footer__title {
  font-family: $main-font-family;
  color: $text-color;
  font-size: 18px;
  line-height: 1.16;
  text-transform: uppercase;

  margin-bottom: 17px;

  @media screen and (min-width: 768px) and (max-width: 1199.98px) {
    font-size: 26px;
    line-height: 1.15;
    letter-spacing: 0.04em;

    margin-bottom: 16px;
  }

  @media screen and (min-width: 1200px) {
    font-size: 30px;
    line-height: 1.13;

    margin-bottom: 15px;
  }
}

.footer__title--red {
  color: $brand-color;
  font-size: 34px;
  line-height: 1.14;
  letter-spacing: 0.04em;

  @media screen and (min-width: 768px) and (max-width: 1199.98px) {
    font-size: 48px;
    letter-spacing: 0.06em;
  }

  @media screen and (min-width: 1200px) {
    font-size: 58px;
    line-height: 1.13;
  }
}

.social-media__link {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  border-radius: 50%;
  color: $background-color;
  background-color: $icons-color;

  @media screen and (min-width: 768px) {
    width: 34px;
    height: 34px;
  }

  transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1),
    color 250ms cubic-bezier(0.4, 0, 0.2, 1);

  &:hover,
  &:focus {
    background-color: $brand-color;
  }
}

.social-media__icon {
  fill: currentColor;
}

.footer__address {
  font-family: $secondary-font-family;
  color: $main_text-color;
  font-style: normal;
  text-decoration: none;
  font-weight: 700;
  font-size: 12px;
  line-height: 1.86;

  @media screen and (min-width: 768px) and (max-width: 1199.98px) {
    font-size: 14px;
    line-height: 1.85;
  }

  @media screen and (min-width: 1200px) {
    font-size: 16px;
    line-height: 1.87;
    letter-spacing: 0.02em;
  }
}

.address__link {
  color: $brand-color;
}

.footer__copyright {
  font-family: $secondary-font-family;
  color: $discription_text-color;
  font-weight: 700;
  font-size: 13px;
  line-height: 1.47;

  text-align: center;

  @media screen and (min-width: 768px) and (max-width: 1199.98px) {
    font-size: 14px;
    line-height: 1.85;
    letter-spacing: 0.02em;
  }
}

.footer__sub-block {
  @media screen and (min-width: 768px) {
    display: flex;
    justify-content: space-between;
  }
  @media screen and (min-width: 768px) and (max-width: 1199.98px) {
    margin-bottom: 90px;
  }
  @media screen and (min-width: 1200px) {
    margin-bottom: 97px;
  }
}
.footer__name-block {
  @media screen and (max-width: 767px) {
    max-width: 232px;
    margin-bottom: 43px;
  }

  @media screen and (min-width: 768px) and (max-width: 1199.98px) {
    max-width: 340px;
  }
}

.social-media__list {
  display: flex;
  justify-content: flex-end;
  margin-bottom: 46px;
}

.social-media__item:not(:last-child) {
  margin-right: 28.24px;
}

.footer__address {
  text-align: end;
  margin-bottom: 43px;

  @media screen and (min-width: 768px) {
    margin-bottom: 0px;
  }
}

.footer__copyright-block {
  padding-bottom: 25px;
  padding-top: 25px;
  padding-left: 60px;
  padding-right: 60px;

  border: 1px solid #e1e1e1;
  border-bottom: none;
  border-left: none;
  border-right: none;

  @media screen and (min-width: 768px) and (max-width: 1199.98px) {
    padding-left: 0px;
    padding-right: 0px;
  }
}
